mapper.select:
COUNT (object_anwser1, object_anwser2, object_answer3, object_answer4, object_answer5)
FROM surveyObject
WHERE survey_year = #{survey_year} AND survey_semester = #{survey_semester}

SELECT *
FROM surveyObject
WHERE survey_year = #{survey_year} AND survey_semester = #{survey_semester}

SELECT object_question
FROM surveyObject
WHERE survey_year = #{survey_year} AND survey_semester = #{survey_semester}

mapper.update:
  	UPDATE surveyObject
  	SET object_answer1 = object_answer1 + 1
	WHERE question_id = #{question_id}

  	UPDATE surveyObject
  	SET object_answer2 = object_answer2 + 1
	WHERE question_id = #{question_id}

  	UPDATE surveyObject
  	SET object_answer3 = object_answer3 + 1
	WHERE question_id = #{question_id}

  	UPDATE surveyObject
  	SET object_answer4 = object_answer4 + 1
	WHERE question_id = #{question_id}

  	UPDATE surveyObject
  	SET object_answer5 = object_answer5 + 1
	WHERE question_id = #{question_id}
