mapper.select:
SELECT user_name
FROM user
WHERE user_id = #{user_id}

mapper.update:
UPDATE user
SET user_password = #{user_password},
	login_record = #{login_record}
WHERE user_id = #{user_id}

UPDATE user
SET user_auth = 1
WHERE user_id = #{user_id}

UPDATE user
SET user_auth = 2
WHERE user_id = #{user_id}

UPDATE user
SET user_auth = 0 
WHERE user_id = #{user_id}

UPDATE user
SET user_auth = 3
WHERE user_id = #{user_id}

UPDATE user
SET user_auth = 4
WHERE user_id = #{user_id}

mapper.select:
SELECT u.*
FROM user u LEFT JOIN mentoroom m ON u.user_id = m.mento_id
WHERE u.user_auth = 1 AND m.report_count != #{meeting_number}

mapper.update:
UPDATE user
SET report_check = 1
WHERE user_id = #{user_id}

UPDATE user
SET report_check = 0
WHERE user_id = #{user_id}
