mapper.select:
SELECT a.*, u.user_name
FROM article a JOIN user u ON a.article_writer = u.user_id
WHERE id = #{id}

mapper.update:
UPDATE article
SET article_title = #{article_title}, 
  	article_content = #{article_content}
WHERE id = #{id}

mapper.select:
SELECT article_hit
FROM article
WHERE id = #{id}

mapper.update:
 UPDATE article
 SET article_hit = #{article_hit}
	WHERE id = #{id}

UPDATE article
SET answer_complete = 1
WHERE id = #{id}
