mapper.select:
SELECT f.*, m.mento_name
FROM uploadFile f LEFT JOIN mentoroom m ON f.mentoroom_id = m.mentoroom_id
WHERE f.file_kind = 1 AND m.team_year = #{team_year} AND m.team_semester = #{team_semester}

mapper.update:
UPDATE uploadFile
SET file_state = 1
WHERE id = #{id}

UPDATE uploadFile
SET file_state = 2
WHERE id = #{id}
